import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    CategoryId: 0,
    ort: {
        name: 'популярности', sortProperty: 'rating'
    }
}

const filterSlice = {
    name: 'CategoryId&Sort',
    initialState,
    reducers: {
        setCategoryId(state, action) {
            state.CategoryId = action.plyload
        }
    }
};

export default filterSlice.reducers

export const { setCategoryId } = filterSlice.reducers
