import { PizzaCartType } from '../Redux/Slices/cartSlice'

// первым аргументом мы указываем с какой ветки обьекта нашего стейта мы будем брать данные, вторым передаем под каким ключем эти данные устанавливать в локал сторидж. 
export const setLSCart = (data: string, state: PizzaCartType[] | number,) => {
    const json = JSON.stringify(state)
    localStorage.setItem(`${data}`, json)
}
