import { sumPrice } from './totalPrice'
import { PizzaCartType } from '../Redux/Slices/cartSlice'

export const setInitionalCart = () => {

    const data = localStorage.getItem('cart')
    const items = data ? JSON.parse(data) : []
    const totalPrice = sumPrice(items)
    const totalItems = data ? JSON.parse(data).reduce((acc: number, el: PizzaCartType) => {
        return acc += el.count
    }, 0) : 0

    return {
        items,
        totalPrice,
        totalItems
    }

}
