import React from "react";
import { useSelector, useDispatch } from "react-redux";
import s from './Search.module.scss';
import { debounce } from 'lodash'
import { setSearchValue } from '../../Redux/Slices/filterSlice'

function Search() {

    const dispatch = useDispatch()
    const searchValue = useSelector((state: any) => state.filterSlice.searchValue)

    const [value, setValue] = React.useState<string | undefined>('')
    const inputRef = React.useRef<HTMLInputElement | null>(null)
    // вместо MouseEventHandler ставим MouseEvent
    function clearValueInput(event: React.MouseEvent<SVGSVGElement>) {
        dispatch(setSearchValue(''))
        setValue('')
        inputRef.current?.focus()
    }

    const updateSearchValue = React.useCallback(
        debounce((str: string) => {
            dispatch(setSearchValue(str))
        }, 300), []
    )
    // вместо React.ChangeEventHandler ставим React.ChangeEvent
    function onChangeInput(event: React.ChangeEvent<HTMLInputElement>) {

        setValue(event.target.value)
        updateSearchValue(event.target.value)
    }

    return (
        <div className={s.root}>
            <svg className={s.icon} enableBackground="new 0 0 32 32" id="Glyph" version="1.1" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg" ><path d="M27.414,24.586l-5.077-5.077C23.386,17.928,24,16.035,24,14c0-5.514-4.486-10-10-10S4,8.486,4,14  s4.486,10,10,10c2.035,0,3.928-0.614,5.509-1.663l5.077,5.077c0.78,0.781,2.048,0.781,2.828,0  C28.195,26.633,28.195,25.367,27.414,24.586z M7,14c0-3.86,3.14-7,7-7s7,3.14,7,7s-3.14,7-7,7S7,17.86,7,14z" id="XMLID_223_" /></svg>
            <input ref={inputRef} value={value} onChange={(e) => onChangeInput(e)} className={s.input} type="text" placeholder="Поиск пицц" />
            {searchValue && (<svg className={s.icon__close} onClick={clearValueInput} height="512px" id="Layer_1" version="1.1" viewBox="0 0 512 512" width="512px"
                xmlns="http://www.w3.org/2000/svg" ><g><path d="M256,33C132.3,33,32,133.3,32,257c0,123.7,100.3,224,224,224c123.7,0,224-100.3,224-224C480,133.3,379.7,33,256,33z    M364.3,332.5c1.5,1.5,2.3,3.5,2.3,5.6c0,2.1-0.8,4.2-2.3,5.6l-21.6,21.7c-1.6,1.6-3.6,2.3-5.6,2.3c-2,0-4.1-0.8-5.6-2.3L256,289.8   l-75.4,75.7c-1.5,1.6-3.6,2.3-5.6,2.3c-2,0-4.1-0.8-5.6-2.3l-21.6-21.7c-1.5-1.5-2.3-3.5-2.3-5.6c0-2.1,0.8-4.2,2.3-5.6l75.7-76   l-75.9-75c-3.1-3.1-3.1-8.2,0-11.3l21.6-21.7c1.5-1.5,3.5-2.3,5.6-2.3c2.1,0,4.1,0.8,5.6,2.3l75.7,74.7l75.7-74.7   c1.5-1.5,3.5-2.3,5.6-2.3c2.1,0,4.1,0.8,5.6,2.3l21.6,21.7c3.1,3.1,3.1,8.2,0,11.3l-75.9,75L364.3,332.5z" /></g></svg>)}
        </div>
    )
}

export default Search
