import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    totalPrice: 0,
    totalItems: 0,
    items: [],

}

const cartSlice = createSlice({
    name: 'cartSlice',
    initialState,
    reducers: {
        addItem(state, action) {
            console.log(state.items.target)
            const found = state.items.find(obj => obj.id === action.payload.id)
            if (found) {
                found.count++
            } else {
                state.items.push({
                    ...action.payload,
                    count: 1
                })
            }
            state.totalPrice = state.items.reduce((acc, el) => {
                return acc += (el.price * el.count)
            }, 0)

            state.totalItems = state.items.reduce((acc, el) => {
                return acc += el.count
            }, 0)

        },
        minusItem(state, action) {
            const found = state.items.find(obj => obj.id === action.payload)
            if (found) {
                found.count--
            }

            state.totalPrice = state.items.reduce((acc, el) => {
                return acc += (el.price * el.count)
            }, 0)

            state.totalItems = state.items.reduce((acc, el) => {
                return acc += el.count
            }, 0)
        },
        deleteItem(state, action) {
            state.items = state.items.filter((obj) => obj.id !== action.payload)
        }
    }
})

export const { addItem, minusItem } = cartSlice.actions
export default cartSlice.reducer
