import React from "react";

function Categories() {

    const [activeIndex, setIndex] = React.useState(5)

    function onClickSetIndex(ind) {
        setIndex(ind)
    }

    const CategoriesArrTittle = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые']

    let CategoriesArr = CategoriesArrTittle.map((el, ind) => {
        return <li onClick={(() => onClickSetIndex(ind))} className={activeIndex === ind ? 'active' : ''}>el</li>
    }
    )

    return (
        <div className="categories">
            <ul>
                {CategoriesArr}
            </ul>
        </div>
    )
}

export default Categories
