import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from 'axios'

const fetchPizzas = createAsyncThunk('pizza/fetchPizzasByIdStatus',
    async ({ order, sortPut, category, search, currentPage }) => {
        const { data } = await axios.get(`https://63a4cc372a73744b00802459.mockapi.io/items?page=${currentPage}&limit=4${category}&sortBy=${sortPut}&order=${order}&${search}`)
        setPizza(data)
    }
)

const initialState = {
    pizzas: [],
}

const pizzaSlice = createSlice({
    name: 'pizzas',
    initialState,
    reducers: {
        setPizza(state, action) {
            state.pizzas = action.payload
        }
    }
})

export { fetchPizzas }
export const { setPizza, } = pizzaSlice.actions
export default pizzaSlice.reducer
