import React from "react";

function Categories() {

    const [activeIndex, setIndex] = React.useState(5)

    function onClickSetIndex(ind) {
        setIndex(ind)
    }

    return (
        <div className="categories">
            <ul>
                <li onClick={onClickSetIndex} className={activeIndex === 0 ? 'active' : ''}>Все</li>
                <li onClick={onClickSetIndex} className={activeIndex === 1 ? 'active' : ''}>Мясные</li>
                <li onClick={onClickSetIndex} className={activeIndex === 2 ? 'active' : ''}>Вегетарианская</li>
                <li onClick={onClickSetIndex} className={activeIndex === 3 ? 'active' : ''}>Гриль</li>
                <li onClick={onClickSetIndex} className={activeIndex === 4 ? 'active' : ''}>Острые</li>
                <li onClick={onClickSetIndex} className={activeIndex === 5 ? 'active' : ''}>Закрытые</li>
            </ul>
        </div>
    )
}

export default Categories
