import React from "react";

function Categories() {

    const titleItemsArr = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые']
    const [countItem, setItem] = React.useState(0)
    const titleRendering = titleItemsArr.map((el, ind) => {
        return <li onClick={() => setItem(ind)} className={countItem === ind ? 'active' : ''}> {el} </li>
    })

    return (
        <div className="categories">
            <ul>
                {titleRendering}
            </ul>
        </div>
    )
}

export default Categories
