import React, { useEffect } from "react";
import { setSort } from '../Redux/Slices/filterSlice'
import { useSelector, useDispatch } from 'react-redux'

type listObjectType = {
    name: string,
    sortProperty: string
}

const list: listObjectType[] = [
    { name: 'популярности (ASK)', sortProperty: 'rating' },
    { name: 'популярности (DESC)', sortProperty: '-rating' },
    { name: 'цене (DESC)', sortProperty: 'price' },
    { name: 'цене (ASK)', sortProperty: '-price' },
    { name: 'алфавиту (DESC)', sortProperty: 'tittle' },
    { name: 'алфавиту (ASK)', sortProperty: '-tittle' }
]


const Sort: React.FC = () => {
    const dispatch = useDispatch()
    // @ts-ignore
    const sort: listObjectType = useSelector(state => state.filterSlice.sort)
    const [turn, turnChenge] = React.useState(false)
    const sortRef = React.useRef<HTMLDivElement | null>(null)

    React.useEffect(() => {
        function toggleSort(event: MouseEvent) {

            // const _event = event as MouseEvent & {
            //     path: Node[]
            // }
            console.log(event.target)
            if (

                sortRef.current && !sortRef.current.contains(event.target)) {
                turnChenge(false)
            }

        }
        const body: HTMLBodyElement | null = document.querySelector('body')
        body?.addEventListener('click', toggleSort)

        return function () {
            body?.removeEventListener('click', toggleSort)
        }

    }, [])

    return (
        <div className="sort">
            <div ref={sortRef} className="sort__label">
                <svg
                    width="10"
                    height="6"
                    viewBox="0 0 10 6"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                >
                    <path
                        d="M10 5C10 5.16927 9.93815 5.31576 9.81445 5.43945C9.69075 5.56315 9.54427 5.625 9.375 5.625H0.625C0.455729 5.625 0.309245 5.56315 0.185547 5.43945C0.061849 5.31576 0 5.16927 0 5C0 4.83073 0.061849 4.68424 0.185547 4.56055L4.56055 0.185547C4.68424 0.061849 4.83073 0 5 0C5.16927 0 5.31576 0.061849 5.43945 0.185547L9.81445 4.56055C9.93815 4.68424 10 4.83073 10 5Z"
                        fill="#2C2C2C"
                    />
                </svg>
                <b>Сортировка по:</b>
                <span onClick={() => turnChenge(!turn)}> {sort.name} </span>
            </div>
            {turn && (<div className="sort__popup">
                <ul>
                    {list.map((obj, idx) => {
                        return <li onClick={(obj) => {
                            turnChenge(false)
                            dispatch(setSort(list[idx]))
                        }}
                            className={obj.sortProperty === sort.sortProperty ? 'active' : ''} key={obj.name}>{obj.name}</li>
                    })}
                </ul>
            </div>)}
        </div>
    )
}


export default Sort
export { list }
